#include <stdint.h>
#include <led.h>
#include <stdio.h>
#include <buzzer.h>
#include <melody.h>

void delay_ms(uint32_t milsecond);
void buzzerSetState(uint8_t logic);



int main(void)
{
	uint8_t b  = 0;
	BuzzerControl_Init();
    /* Loop forever */
	while(1)
	{
		// Bật led bằng cách lấy giá trị a sử dụng toán tử OR để cộng với  (1 << 0)
		buzzerSetState(b |= (1 << 0));
		delay_ms(500);// Tạo độ trễ T= 500ms hay 0.5s
		// Tắt led bằng cách lấy giá trị a sử dụng toán tử AND để nhân với giá trị đảo (1 << 0)
		buzzerSetState(b &= ~(1 << 0));
		delay_ms(500);
	}
}

void delay_ms(uint32_t milsecond)
{
	for(uint32_t i=0; i < milsecond; i++)
	{
		for(uint32_t j=0; j < 5000; j++);
	}
}

void buzzerSetState(uint8_t logic)
{
	if(logic == 1)
	{
		BuzzerControl_SetMelody(pbeep);
	}
	else
	{
		BuzzerControl_SetMelody(0);
	}
}
