#include <stdint.h>
#include "led.h"
#include "button.h"


int main(void)
{
	LedControl_Init();
	Button_Init();
    /* Loop forever */
	while(1)
	{
		// Sử dụng câu lệnh điều kiện if để kiểm tra trạng thái nút ấn trên mạch khi được ấn.
		if (Button_GetLogicInputPin(BUTTON_BOARD_ID) == 0)
		{LedControl_SetColorGeneral(LED_BOARD_ID, LED_COLOR_GREEN, 100);}
		// Điều khiển led trên Board STM32 NUCLEO sáng ở cường độ 100%.
		LedControl_SetColorGeneral(LED_BOARD_ID, LED_COLOR_GREEN, 0);
	}
}
