#include <stdint.h>
#include <led.h>
#include <stdio.h>

void delay_ms(uint32_t milsecond);



int main(void)
{
	LedControl_Init();
    /* Loop forever */
	while(1)
	{
		LedControl_SetColorIndividual(LED_BOARD_ID, LED_COLOR_GREEN, 100);
		// Điều khiển bật led trên Board STM32 NUCLEO
		delay_ms(500);// Tạo độ trễ T= 500ms hay 0.5s
		LedControl_SetColorIndividual(LED_BOARD_ID, LED_COLOR_GREEN, 0);
		// Điều khiển tắt led trên Board STM32 NUCLEO
		delay_ms(500);
	}
}

void delay_ms(uint32_t milsecond)
{
	for(uint32_t i=0; i < milsecond; i++)
	{
		for(uint32_t j=0; j < 5000; j++);
	}
}
