#include <stdint.h>
#include <stdio.h>

 unsigned int global = 10;

int main(void)
{
	static unsigned int local = 100;

	printf("value of global variable: %i \n",global);
	printf("value of local variable: %i \n",local);

	printf("address of global variable: %p \n",&global); //0x20000000
	printf("address of local variable: %p \n",&local); //0x20000004

    /* Loop forever */
	for(;;);
}
