#include <stdint.h>
#include "led.h"
#include "button.h"
#include "eventbutton.h"
#include "stm32f401re_gpio.h"


int main(void)
{
	LedControl_Init();
	Button_Init();
	EventButton_Init();
    /* Loop forever */
	while(1)
	{
		// Đọc trạng thái nút ấn trên PORT C của chân 13 xem có được nhấn không.
		if (GPIO_ReadInputDataBit(GPIOC, GPIO_Pin_13) == 0)
		{LedControl_SetColorGeneral(LED_KIT_ID0, LED_COLOR_GREEN, 100);}
		// Điều khiển led trên Board STM32 NUCLEO sáng ở cường độ 100%.
		LedControl_SetColorGeneral(LED_KIT_ID0, LED_COLOR_GREEN, 0);
	}
}


