#include <stdint.h>
#include <string.h>
#include <stdio.h>
#include "Ucglib.h"
#include "timer.h"
#include "stm32f401re_adc.h"
#include "lightsensor.h"

#define CYCLE_SEND_DATA 1000 // ms
void AppInitCommon(void);
void processGetValueSensor(void);

//● time_current: thời gian hiện tại.
//● time_initial: thời gian lúc khởi tạo.
//● time_total: tổng thời gian tích lũy.
//	Private variable ---------------------------
	static uint32_t time_current, time_initial;
	static uint32_t time_total;
	static uint32_t light;
	static ucg_t ucg;
	static char src3[20] = "";


int main(void)
{

	AppInitCommon();

    /* Loop forever */
	while(1)
	{
		processGetValueSensor();
		processTimerScheduler();//// Tính toán và xử lý thời gian.
	}
}


void AppInitCommon(void)
{
	SystemCoreClockUpdate(); // Khởi tạo xung nhịp hoạt động của hệ thống.
	LightSensor_Init(ADC_READ_MODE_POLLING);// Cấu hình các chân GPIO của ngoại vi adc và cài đặt ADC ở chế độ quét liên tục.
	TimerInit();
	time_initial = GetMilSecTick();// lấy thời gian lúc cắm nguồn làm mốc

	Ucglib4WireSWSPI_begin(&ucg, UCG_FONT_MODE_SOLID);// Cấu hình các chân giao tiếp SPI của STM32 với màn hình LCD.
	ucg_ClearScreen(&ucg);// Xóa màn hình hiển thị LCD.
	ucg_SetFont(&ucg, ucg_font_ncenR12_hr);// Cài đặt font chữ kiểu ncenR12_hr để hiển thị.
	ucg_SetColor(&ucg, 0, 255, 255, 255);// Set màu trắng cho text hiển thị.
	ucg_SetColor(&ucg, 1, 0, 0, 0);// Set màu đen cho nền hiển thị (background).
	ucg_SetRotate180(&ucg);// Xoay màn hình một góc 180 độ.

	}


/**
 * @func   LightSensor_pollingRead
 * @brief  Measure adc value in mode polling
 * @param none
 * @retval value of ADC
 */
uint16_t LightSensor_pollingRead(void)
{
	uint16_t result = 0;

	// Start ADCx software conversion
	ADC_SoftwareStartConv(ADCx_SENSOR);// Cho phép ngoại vi ADC1 bắt đầu chuyển đổi dữ liệu.

	ADC_GetFlagStatus(ADCx_SENSOR, ADC_FLAG_EOC); // Đợi cho quá trình chuyển đổi dữ liệu hoàn tất.

	result = ADC_GetConversionValue(ADCx_SENSOR); // Đọc giá trị từ cảm biến ánh sáng.
	return result;
}

/**
 * @func   processGetValueSensor
 * @brief  Scan value of sensor
 * @param none
 * @retval None
 */
void processGetValueSensor(void)
{
	/* Get timer */
	time_current = GetMilSecTick();

	if(time_current >= time_initial)
	{
		time_total += time_current - time_initial;
	}
	else
	{
		time_total += 0xFFFFFFFFU - time_current + time_initial;
	}

	if(time_total >= CYCLE_SEND_DATA)
	{
		time_total =0;
		light = LightSensor_pollingRead();

		//display out
		memset(src3, 0,sizeof(src3));
		sprintf(src3, "Light = %d lux", light);
		ucg_DrawString(&ucg, 0, 32, 0,src3);



	}
	time_initial = time_current;//// Reset lại biến thời gian sau quá trình đọc giá trị nhiệt độ - độ ẩm.
}

